import * as React from 'react';
import { Platform, Dimensions, Alert, Text, View} from "react-native";
import { Appbar, Avatar, Button, Paragraph, Dialog, Portal } from "react-native-paper";
import { SvgUri, SvgCssUri } from "react-native-svg";
import { isMobile, isBrowser } from "react-device-detect";

import { MaterialCommunityIcons } from "@expo/vector-icons";
import Logo from '../components/logo';

export interface AppProps { 
}

const { width, height } = Dimensions.get('window')

const isMobileSz =  width >= 640;



const CustomHeader = ({ scene, previous, navigation }:any) => {
  const { options } = scene.descriptor;
  const title =
    options.headerTitle !== undefined
      ? options.headerTitle
      : options.title !== undefined
      ? options.title
      : scene.route.name;

        const breakpoint =  620;



  return (
    <Appbar.Header style={{ backgroundColor: "red", marginHorizontal: 10 }}>
      {previous && <Appbar.BackAction onPress={navigation.pop} color={"red"} />}
      {width < breakpoint ? (
        <Appbar.Action icon="menu" onPress={() => {}} />
      ) : null}

      <Appbar.Content
        titleStyle={{
          textAlign: Platform.OS === "web" ? "left" : "center",
        }}
        title={
          previous ? (
            title
          ) : (
            // <Text>TEXT</Text>
            <Logo width={260} height={180} />
          )
        }
      />
      {isMobileSz ?? (
        <Appbar.Content
          style={{
            position: "absolute",
            justifyContent: "center",
            right: 0,
          }}
          title={
            <View
              style={{
                flexDirection: "row",
                //justifyContent: "space-around",
              }}
            >
              <Button
                mode="text"
                onPress={() => console.log("Pressed")}
                labelStyle={{ color: "white", fontWeight: "bold" }}
              >
                Home
              </Button>
              <Button
                mode="text"
                onPress={() => console.log("Pressed")}
                labelStyle={{ color: "white", fontWeight: "bold" }}
              >
                Scan
              </Button>
              <Button
                mode="text"
                labelStyle={{ color: "white", fontWeight: "bold" }}
                onPress={() => console.log("Pressed")}
              >
                Timeline
              </Button>
            </View>
          }
        />
      )}
    </Appbar.Header>
  );
};


export default CustomHeader;
